module.exports = function (source, callback) {

	var less = require('less');
	try {
		less.render(source, function (err, content) {
			if (err && callback) {
				callback.apply(this, [err]);
			}
			if (callback) {
				callback.apply(this, [err, content.css]);
			}
		});
	} catch (err) {
		if (err && callback) {
			callback.apply(this, [err]);
		}
	}

};